about.usage:
// Wrap in ThemeProvider
import { TrendingUp, ShoppingCart, Users, Globe } from 'lucide-react';

<ThemeProvider defaultButtonVariant="icon-arrow" defaultTextAnimation="reveal-blur" borderRadius="soft" background="aurora" cardStyle="glass-elevated" primaryButtonStyle="gradient" secondaryButtonStyle="glass">
  <AboutMetric 
    title="At Berco Inc. we redefine innovation by empowering businesses to reach their full potential" 
    useInvertedBackground={"noInvert"}
    metrics={[
      { icon: TrendingUp, label: 'Conversions', value: '7,000+' },
      { icon: ShoppingCart, label: 'Ecommerce Since', value: '2018' },
      { icon: Users, label: 'Partnered Brands', value: '30+' },
      { icon: Globe, label: 'Global Impressions Generated Annually', value: '100M+' }
    ]} 
  />
</ThemeProvider>

feature.usage:
// Wrap in ThemeProvider
import { Sparkles } from 'lucide-react';

<ThemeProvider defaultButtonVariant="text-stagger" defaultTextAnimation="entrance-slide" borderRadius="rounded" cardStyle="solid" primaryButtonStyle="gradient" secondaryButtonStyle="glass">
  <FeatureProcessSteps
    tag="How we work"
    tagIcon={Sparkles}
    title="Getting you results without the complexity"
    description="Our three-step process takes you from identifying opportunities to launching systems."
    buttons={[{ text: 'Book a call', href: '#' }, { text: 'Watch video', href: '#' }]}
    steps={[
      { number: '01', title: 'Discovery & Assessment', tag: 'Week 1', description: 'We analyze your workflows and identify opportunities.' },
      { number: '02', title: 'Build & Deploy', tag: 'Weeks 2-4', description: 'We create and launch custom systems tailored to your operations.' },
      { number: '03', title: 'Train & Support', tag: 'Ongoing', description: 'We train your team and provide ongoing support.' }
    ]}
    useInvertedBackground="noInvert"
  />
</ThemeProvider>
